SELECT * FROM t_sales


        userid=#{userid}


        name LIKE concat('%',#{name},'%')


        and progress = #{progress}


        and date(lasttime) >= #{startdate}


        and date(lasttime) <= #{enddate}


order by id desc
limit ${start},${length}

SELECT user.realname,(SELECT ifnull(sum(price),0) FROM
t_sales WHERE userid = user.id and date(successtime) >= #{start}
and date(successtime) <= #{end} )as price from (SELECT id,realname FROM t_user) as user
